#!/usr/bin/env bashUNITY_EMAIL=${UNITY_EMAIL:-}UNITY_PASSWORD=${UNITY_PASSWORD:-}# Return licenseunity-editor \    -logFile /tmp/unity-return.log \    -quit \    -returnlicense \    -username "$UNITY_EMAIL" \    -password "$UNITY_PASSWORD" \    -projectPath /home/jenkins/BlankProject# Store the exit code from the verify commandUNITY_EXIT_CODE=$?# Check if UNITY_EXIT_CODE is 0if [[ $UNITY_EXIT_CODE -eq 0 ]]then  echo "Return successful"else  echo "Return failed"fi